# """.strip

string = File.read('4.input.txt').strip

matrix = string.split("\n").map { |x| x.split('') }

def find_character_positions_in_matrix(matrix, character)
  positions = []
  matrix.each_with_index do |row, row_index|
    positions += row.each_index.select { |index| row[index] == character}.map { |index| { x: index, y: row_index } }
  end

  positions
end

def find_string_in_array(array, string)
  joined_array = array.join('')

  joined_array.scan(/#{string}/).length > 0 || joined_array.scan(/#{string.reverse}/).length > 0
end


def find_diagonal_word(matrix)
  positions = find_character_positions_in_matrix(matrix, "A")

  count = 0

  positions.each do |position|
    x = position[:x]
    y = position[:y]

    next if x.zero? || y.zero? || x == matrix[0].length - 1 || y == matrix.length - 1

    diagonal_1 = [
      matrix[y-1][x-1],
      matrix[y][x],
      matrix[y+1][x+1],
    ]

    diagonal_2 = [
      matrix[y-1][x+1],
      matrix[y][x],
      matrix[y+1][x-1],
    ]

    count += 1 if find_string_in_array(diagonal_1, "MAS") && find_string_in_array(diagonal_2, "MAS")
  end

  count
end

puts find_diagonal_word(matrix).inspect

